def count(tab)
nb=0
tab.each do |element|
	nb += 1
end

puts "Le tableau contient #{nb} éléments."
end

def shortest_handle(tab)
min=100
shortest_handle =""
tab.each do |handle|
	nb_char=0
	handle.each_char do |char|
		nb_char += 1
	end
	if nb_char < min
		min = nb_char
		shortest_handle = handle
	end
end
puts "Le handle le plus court est #{shortest_handle}."
end

def longest_handle(tab)
max=0
longest_handle =""
tab.each do |handle|
	nb_char=0
	handle.each_char do |char|
		nb_char += 1
	end
	if nb_char > max
		max = nb_char
		longest_handle = handle
	end
end
puts "Le handle le plus long est #{longest_handle} avec #{max} caractères."
end

def return_max(tab)
max=0
longest_handle =""
tab.each do |handle|
	nb_char=0
	handle.each_char do |char|
		nb_char += 1
	end
	if nb_char > max
		max = nb_char
		longest_handle = handle
	end
end
return max
end

def five_char(tab)

count = 0	
tab.each do |handle|
	nb_char=0
	handle.each_char do |char|
		nb_char += 1
	end
	if nb_char == 6
		count += 1
	end
end

puts "Il y a en tout #{count} handles constitués de cinq caractères."
end

def is_first_char_cap(tab)

count=0
tab.each do |handle|
	char=handle[1]
	cap=char.downcase
	if cap != char
		count += 1
	end
end

puts "Il y a exactement #{count} handles dont le premier caractère est une majuscule."
end

def sort_alphabetically(tab)

sorted_tab = tab.sort_by(&:downcase)

puts "Liste des handles par ordre alphabétique : "
puts sorted_tab
end

def sort_by_length(tab)

sorted_tab = tab.sort_by(&:length)

puts "Liste des handles par ordre de longueur : "
puts sorted_tab
end

def search_handle(tab)
print "De quel handle soutaitez-vous connaître l'index ? "
handle=gets.chomp
count = 0

tab.each do |element|
	if element == handle
		break
	else
		count += 1
	end
end

puts "Le handle #{handle} est situé à l'index #{count}."
end


def sort_by_length(tab)

nb = []

max_len=return_max(tab)

max_len.times do |i|
    nb << 0
end

tab.each do |element|
        count=0
        element.each_char do
                count += 1
        end
	for i in (1...max_len)
        if count == i
          nb[i]+=1
        end
      end

end

puts "La liste contient :"
for i in (1..max_len)
  puts " > #{nb[i]} élément(s) contenant #{i} caractères"

end
end


